                        <Nav className='ml-auto' navbar>
                            <NavItem active>
                                <NavLink href='/' >Home</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink href='/'>About</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink href='/'>Our Team</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink href='/'>Blogs</NavLink>
                            </NavItem>
                        </Nav>
                    </Collapse>

                </Navbar>
            </div>
        );

