                $('#logos').empty();
                $('#logos').prepend(`<img id = 'desktop' src=${logo_desktop} width='290px' height='80px' className = 'd-inline-block align-top desktop' alt=""/>`);
            }

        }


        window.onresize = changeLogo;
        window.onLoad = changeLogo;
        const { navCollapsed } = this.state;




