import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; 
import  {
    Card,
    Button
} from 'react-bootstrap';

import $ from 'jquery';

class RecruiterCard extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            modal: false,
        };

        this.toggle = this.toggle.bind(this);

    }

    toggle() {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
    }

    render() {
        console.log(this.props);
        return (
            <div id="recruiter" className='align-items-center'>
                <Card style={{ width: '18rem' }}>
                  <Card.Img variant="top" src={'http://192.168.56.101:1337' + this.props.Headshot.url} />
                  <Card.Body>
                    <Card.Title>{this.props.FirstName + ' ' + this.props.LastName}</Card.Title>
                    <Card.Text>
                      <FontAwesomeIcon icon='linkedin' />
                    </Card.Text>
                    <Button variant='danger' >Schedual a Call/Coffee Chat</Button>
                  </Card.Body>
                </Card>

            </div>
        )
    }

}

export default RecruiterCard;
